import { gql } from '@apollo/client';
import React from 'react';
import { Card, Table } from 'react-bootstrap';

const CONTROL_POINT_STATUS_QUERY = gql`
query ControlPointStatusQuery {
    allControlPoints {
      edges {
        node {
          name
          online
        }
      }
    }
  }
`

class ControlPointStatus extends React.Component {

    render() {
        return (
            <Card className="h-100">
              <Card.Body>
                <Card.Title>Control Point Status</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">All Raspberry Pi's are responding.</Card.Subtitle>
                <Table striped bordered hover className="control-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>City Control 1</td>
                      <td>
                        <div class="led-box">
                            <div class="led-green"></div>
                        </div>
                      </td>
                    </tr> 
                    <tr>
                      <td>Airport</td>
                      <td>
                        <div class="led-box">
                            <div class="led-yellow"></div>
                        </div>
                      </td>
                    </tr>
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
        );
    }
}

export default ControlPointStatus;
