import React from 'react';
import { Form, Button, Card } from 'react-bootstrap';

const CONTROL_POINT_STATUS_QUERY = gql`
  mutation AllLightsBrightnessMutation($brightness: Int!) {
    allLightsBrightnessMutation(brightness: $brightness){
      lightStrips {
        name
        id
      }
    }
  }
`

function AllLights() {
    const { loading, error, data } = useQuery(CONTROL_POINT_STATUS_QUERY);

    if (loading) return 'Loading...';
    if (error) return `Error! ${error.message}`;

    return (
        <Card className="h-100">
            <Card.Body>
            <Card.Title>All Lights</Card.Title>

            <Form>
                <div className="on-off-buttons d-grid gap-2">
                <Button variant="primary" size="lg">On</Button>
                <Button variant="secondary" size="lg">Off</Button>
                </div>

                <Form.Label>Brightness</Form.Label>
                <Form.Range />
            </Form>
            </Card.Body>
        </Card>
    );
}

export default AllLights;
